INSERT INTO `douong` (`MaDU`, `TenDU`, `NguyenLieu`, `CachLam`, `GioiThieu`, `Dongia`, `Anh`, `MaDM`) VALUES
(2, 'Thạch Kem Chanh Dây', '- 1 ½ chén nước quả chanh dây (chanh leo)<br/>\r\n- 150ml nước cam tươi <br/>\r\n- 200g đường trắng  <br/>\r\n- 3 lá gelatine, ngâm trong nước lạnh cho mềm Làm kem trang trí: <br/>\r\n- 300ml kem tươi <br/>\r\n- 150ml sữa <br/>\r\n- 3 lòng đỏ trứng<br/> \r\n- 75g đường <br/>- 2 thìa rượu Rum <br/>\r\n- 2 lá gelatine <br/>\r\n- Xoài, chanh dây, chuối dâu tây trang trí (bất cứ loại trái cây yêu thích nào của bạn, tùy chọn)', '- Trộn nước trái cây và đường với 1 chén nước, cho vào 1 cái nồi và đun đến khi đường tan hết. Sau đó lọc qua 1 cái rây.\r\n\r\n\r\n- Lá gelatine ngâm mềm, bóp ra với nước rồi cho vào nồi, thêm nửa chén nước quả ban đầu vào đun cùng, ngoáy đều cho gelatine tan hết thì cho nốt hỗn hợp nước quả vào đun. Khuấy cho các nguyên liệu hòa quyện với nhau. Tắt bếp, để nguội bớt rồi rót vào các ly thủy tinh, để nguội hẳn rồi cho vào tủ lạnh khoảng 4 giờ đến khi đông lại.\r\nLưu ý chỉ rót đến nửa ly vì còn chừa chỗ cho phần kem.\r\nLàm kem:\r\n\r\n- Cho lá gelatine vào ngâm trong nước lạnh đến khi mềm\r\n\r\n- Tách đôi quả vani, cho hạt và thịt quả vào nồi, cho kem tươi và sữa vào, đặt lên bếp, để lửa trung bình và đun sôi.\r\n\r\nTrong lúc chờ sữa sôi, bạn đập trứng ra bát lấy lòng đỏ, trộn lòng đỏ trứng với đường, đánh cho trứng bông nổi lên. \r\n\r\n\r\n- Tiếp đến rót hỗn hợp kem tươi sữa nóng vào trứng, tiếp tục đánh. Khi đánh trứng kem bạn nên cuộn tròn 1 cái khăn rồi để âu lên, một tay rót các nguyên liệu vào và tay kia vẫn có thể quấy liên tục, vừa đỡ nóng mà cái âu lại được cố định.\r\n\r\n\r\n- Đổ hỗn hợp trứng sữa và gelatine vào nồi và đun, ngoáy nhẹ liên tục, đun đến khi thấy hơi sánh lại, bám trên thìa là được. Tắt bếp nhấc xuống, để nguội bớt rồi cho rượu rum vào. Để nguội  hẳn, dùng thìa múc nhẹ nhàng từng thìa kem cho vào các ly thạch. Đặt vào tủ lạnh khoảng 1 giờ cho kem hơi đông lại.\r\n- Khi ăn trang trí với dâu tây và ruột quả chanh dây.\r\n \r\nVới màu sắc vô cùng hấp dẫn, thơm ngọt mùi  trái cây, vị mát lạnh và mềm mịn ly thạch kem cho bạn cảm giác sảng khoái yêu đời.', 'Những ly thạch trái cây nhiều màu luôn hấp dẫn thực khách mọi lứa tuổi, hãy tự tay làm những ly thạch kem thơm ngon đẹp mắt để xua tan cái oi bức lúc thời tiết chuyển mùa nhé.      ', 15000, 'image/IMG_Douong/Thach_KemChanhDay.jpg', 2),
(3, 'Thạch Trái Cam Mát Lạnh ', '-4 quả cam vàng <br>\r\n-2 gói bột gelatin <br>\r\n- Một cốc nước<br>\r\n- Đường hoặc mật ong', '- Bổ đôi quả cam\r\n\r\n- Dùng thìa nạo hết ruột và nước ra một cái bát to hoặc vắt nước cam bằng dụng cụ hỗ trợ, rồi tách hết các các sơ múi còn lại bên trong, cố gắng giữ cho vỏ quả cam nguyên vẹn. \r\n- Ngâm bột gelatin trong nước khoảng 15 phút.\r\n\r\n- Cho nước cam vào nồi đun gần sôi, nếu thấy hơi ít nước bạn có thể cho thêm nước lọc, thêm đường hoặc mật ong nếu muốn ăn ngọt hơn. Sau đó cho chỗ nước gelatin vào nồi nước cam. Ngoáy đều cho tan hết. Đun sôi thì tắt bếp.\r\n\r\n- Để nguội bớt rồi rót hỗn hợp vào từng nửa vỏ cam đã vắt lúc đầu. \r\n- Bạn nên để vỏ cam lên một cái khay hoặc khuôn để cố định, khi đổ thạch vào sẽ dễ dàng và an toàn hơn.\r\n\r\n- Để thạch nguội và đông lại rồi cho vào tủ lạnh, vài giờ sau là bạn có món thạch cam chính hiệu cả về hình dáng và mùi vị.\r\n- Lấy cam ra khỏi tủ lạnh, dùng dao cắt thành miếng hình múi cau, bạn nhớ là dao phải thật sắc nhé.\r\n', '        ', 15000, 'image/IMG_Douong/Douong3.jpg', 1),
(4, 'Thạch Sữa Ướp Lạnh ', '4 thìa bột agar  4 cốc sữa  Một vài giọt phẩm màu thực phẩm, hoặc bạn có thể dùng siro hoa quả để có các màu hồng tím hoặc cam và còn có thêm hương vị.  Đường (lượng đường tùy khẩu vị của bạn)  1 thìa hương liệu vị dâu (tùy chọn)', 'Cho bột agar và đường vào sữa khuấy lên, để vài phút. Cho sữa và agar lên bếp đun sôi.\r\n\r\nVừa đun vừa khuấy đều để thạch không bị vón và dính xuống đáy nồi. Khi hỗn hợp sữa vừa sôi thì bạn nhỏ lửa và đun đến khi agar tan hết. Nếu bạn có dùng hương liệu và phẩm màu thì cho vào bước này rồi tắt bếp nhấc nồi xuống.\r\n\r\nĐổ hỗn hợp ra khay, cốc hoặc khuôn có các hình khác nhau.\r\n\r\nĐể nguội đến khi còn âm ấm thì cho vào tủ lạnh. Để thạch ít nhất 2 giờ cho đông lại.\r\n\r\nDùng dao cắt miếng và thưởng thức.\r\nThêm màu xanh và lá dứa bạn sẽ có ly thạch màu xanh hai lớp, lại có mùi thơm ngọt quyến rũ.\r\n\r\nThạch sữa ướp lạnh là món tráng miệng tuyệt vời cho cả nhà.', 'Thạch sữa ướp lạnh là món tráng miệng tuyệt vời cho cả nhà. Vị mát, ngọt và mùi thơm hấp dẫn của hoa trái mùa hè hòa quện trong món thạch sữa này.  ', 15000, 'image/IMG_Douong/Thach_SuaUopLanh.jpg', 2),
(5, 'Thạch Sữa Đậu Nành ', '2 lít sữa đậu nành (sữa không đường)  3/4 chén đường kính trắng  3 chiếc lá dứa  2 thìa bột thạch agar  Vài miếng hoa quả tùy ý thích của bạn (đào, vải, xoài, cam…)', 'Cho sữa đậu nành, lá dứa và đường vào nồi rồi đun sôi, khuấy đều cho đường tan hết. Đun đến khi mùi lá dứa thơm nức tỏa ra khắp phòng. Vớt lá dứa bỏ đi.\r\n\r\nHòa bột agar ra một chút nước rồi cho vào nồi sữa, khuấy liên tục cho tan hết trong sữa.\r\n\r\nTắt bếp, nhấc nồi xuống. Múc sữa ra khuôn hoặc các cốc nhỏ.\r\n\r\nThêm vài lát hoa quả lên trên rồi để thật nguội, cho vào tủ lạnh 1 giờ cho đông lại.\r\n\r\nLấy ra và thưởng thức.', 'Chỉ cần bằng cách làm đơn giản, món sữa đậu đã trở thành những cốc thạch hoa quả thơm mát và bổ dưỡng rồi.  ', 15000, 'image/IMG_Douong/Thach_SuaDauNanh.jpg', 2),
(6, 'Eggnog - Cocktail Trứng Sữa Mùa Đông', '- 6 quả trứng gà - 250g đường trắng - ½ thìa vanilla - ½ thìa nhục đậu khấu (nutmeg) - 200ml rượu Brandy - 100ml rượu Rum - 500ml kem tươi - 500ml sữa', '- Đập trứng ra bát\r\n- Dùng máy đánh trứng đánh trong vòng 3 phút đến khi trứng bông lên\r\n- Sau đó từ từ cho đường vào, vừa cho vừa đánh trứng tiếp tục.\r\n\r\n- Tiếp đến cho vanilla, bột nhục đậu khấu (nếu có) rồi đánh tiếp 30 giây nữa\r\n- Sau đó cho các loại rượu Brandy, rượu Rum\r\n- Rồi cho sữa, kem tươi vào.\r\n\r\n- Dùng thìa ngoáy đều lên.\r\n- Cho hỗn hợp vào tủ lạnh khoảng nửa giờ.\r\n- Sau nửa giờ ướp lạnh lấy bát ra khỏi tủ, dùng muôi múc ra từng ly thủy tinh đã chuẩn bị trước.\r\n- Rắc một chút nhục đậu khấu lên trên \r\nBạn có thể bỏ bớt thành phần rượu đi, eggnog trở thành món sinh tố bổ dưỡng có thể được thưởng thức quanh năm mà không nhất thiết vào mùa Giáng sinh ', 'Eggnog là thức uống ngọt làm từ sữa, kem tươi, trứng sống, là thứ đồ uống phổ biến trong các ngày lễ Noel và năm mới của người Mỹ và Canada.  ', 35000, 'image/IMG_Douong/CockTail_TrungSuaMuaDong.jpg', 3),
(7, 'Cocktail Cam 3 Tầng', '- 10 múi cam  - 1 khoanh bánh ngọt  - 2 muỗng kem tươi  - Rượu Cointreau  - Vỏ cam tươi cắt nhỏ  - 1 lát cam tươi cắt tròn để trang trí', '- Ngâm múi cam vào rượu Cointreau qua đêm trong bình kín và giữ trong tủ lạnh.\r\n\r\n- Pha 1 muỗng Cointreau với 2 muỗng kem tươi. Nạo vỏ cam vào kem. Đánh kem nổi bọt bằng máy xay tay trong 5 phút cho kem dậy bọt mịn. Để riêng.\r\n\r\n- Dùng ly thủy tinh cao, cho múi cam xuống đáy cùng với nước rượu ngâm. Cắt khoanh bánh thành hình tròn vừa đủ lọt vào giữa li. Nén lát bánh nằm ngay trên phần múi cam vào rượu (Xem hình).\r\n\r\n- Trút kem lên lát bánh, rắc vỏ can cắt nhỏ lên trên. Trang trí lát cam tươi trên miệng ly. Ướp lạnh và thưởng thức.', 'Lạ và hấp dẫn, món uống này sẽ mang lại cho bạn một buổi tối thật thú vị!    ', 35000, 'image/IMG_Douong/Douong7.jpg', 3),
(8, 'Dâu Dầm – Mát Da Mùa Nóng ', '- 3 - 5 trái dâu tươi (hoặc nhiều hơn tùy sở thích) - 2 muỗng cà – phê lưng đường cát trắng - Đá bào hoặc đá viên tinh khiết nhỏ', '- Dâu xắt thành lát mỏng\r\n \r\n\r\nCho dâu và đường vào ly, trộn nhẹ, để vào tủ lạnh khoảng 15 phút cho dâu thấm đường, ra nước và có vị ngọt, sau đó cho đá vào, để vào tủ lạnh thêm 5 phút nữa là dùng được.\r\n\r\nMón dâu dầm đặc biệt không dùng sữa, ít đường, khi uống có cảm giác thanh, mát, rất dễ chịu. Chúc các bạn nữ có thêm món cocktail ưng ý mùa hè.', 'Dâu dầm là món Cocktail đơn giản, dễ làm, không dùng nhiều nguyên liệu, đặc biệt không dùng chất béo. Một ly cocktail mát lạnh vừa dùng giải khát, vừa có công dụng đẹp da, chắc hẳn sẽ mang lại niềm vui cho tất cả mọi người đặc biệt là các bạn nữ.  ', 35000, 'image/IMG_Douong/DauDam_MatDaMuaNong.jpg', 3),
(9, 'Cocktail Chanh Mojito', 'Đầu tiên bạn chuẩn bị chanh tây to, nước trắng, nước đường, đá viên là không thể thiếu được khi pha chế cocktail. Tiếp đến là rượu Rum nhẹ, rượu chan', 'Cắt chanh thành những múi nhỏ. Cho chanh, lá bạc hà, syrup vào cốc rồi dùng thìa dằm ra.\r\n\r\n- Sau đó, không nghiền nát tất cả mà chỉ cần lọc lấy nước bằng một chiếc rây (dụng cụ lọc) nhỏ.\r\n\r\n- Pha loãng hỗn hợp với 1-2 cốc nước trắng sao cho vừa uống.\r\n\r\n- Rót hỗn hợp nước chanh vào 4-6 cốc cao, cho thêm đường. Cho đá.\r\n\r\n- Thêm ½ đến 1 thìa rượu Rum vào mỗi cốc. Chế thêm hỗn hợp nước chanh lạnh lên trên lớp rượu. Cho tối đa ¼ quả chanh nếu muốn vào cốc cho đẹp mắt.', 'Cocktail chanh Mojito là loại đồ uống không thể bỏ qua khi nhắc đến đất nước Cu Ba xinh đẹp. Sinh thời, đại văn hào Ernest Hemingway đã rất “mê mẩn” thức uống tuyệt vời này. Có rất nhiều cách để pha chế Mojito như dùng vang trắng, Vodka lúa mới, Rum chauvet… nhưng có lẽ đúng nhất thì vẫn phải là light Rum.  ', 35000, 'image/IMG_Douong/CockTail_ChanhMojito.jpg', 3),
(10, 'Cocktail Hương Vải Ba Tầng ', '- 10ml siro lựu (hoặc siro hoa hồng)  - 3 quả vải  - 20ml nước xoài ép  - 30ml soda  Nếu muốn uống ngọt nhiều thì các bạn tăng lượng siro lên nhé!', '- Rót 10ml siro lựu vào chiếc ly dài hoặc cốc cao.\r\nThành phần nào có nồng độ đậm đặc nhất thì ta cho xuống dưới, loại nhẹ hơn thì lên trên. Cứ dựa theo nguyên tắc này là chúng mình có thể pha những ly nước nhiều tầng rùi.\r\nBước 2:\r\n- Dầm nát 3 quả vải ở một chiếc cốc khác.\r\n- Sau đó đổ nước xoài ép vào nhé!\r\n- Rồi cho thật nhiều đá vô và khuấy thật đều.\r\nBước 3:\r\n- Dùng một chiếc thìa, nhẹ nhàng rót nước xoài ở trên vào ly.\r\nBước 4:\r\n- Và cũng nhẹ nhàng rót soda\r\nBước 5:\r\n- Dùng tăm xiên một quả vải vào rùi đặt ngang miệng ly cho đẹp\r\nNgoài ra, còn nhiều cách décor lắm đó!\r\nVài cánh hoa hồng trôi lững lờ thì sao nhỉ?\r\nVà đôi khi là hoa tươi thì càng đẹp!\r\nHay đơn giản nhất là trang trí với một lát trái cây đó!', 'Làm thế nào để rót được ba tầng như thế nhỉ?    ', 35000, 'image/IMG_Douong/Douong10.jpg', 3),
(11, 'Cocktail Tình Yêu ', 'Dâu tây: 5 quả ép lấy nước  Rượu gin: 30ml  Apricor brandy: 15ml  Nước cốt chanh: 5ml  Sirô lựu: 5ml', 'Cho tất cả vào shaker, lắc đều cùng đá, rồi rót ra ly có chân cao, uống lạnh.', 'Hương thơm tươi mát ngọt ngào làm tinh thần sảng khoái, lượng vitamin dồi dào và màu đỏ quyến rũ là những lý do làm cho trái dâu trở thành sự lựa chọn đầu tiên trong các loại trái cây làm món thức uống cho bữa tiệc tình yêu.    ', 35000, 'image/IMG_Douong/Douong11.jpg', 3),
(13, 'Chè Xòai Mát Lạnh Cho Ngày Hè Nóng Nực', ' - 2 quả xoài chín ngọt  - 100g bột báng  - 150ml nước cốt dừa', '1. Xoài cắt vuông nhỏ để lại 1/3. Chỗ còn lại cho vào máy xay nhuyễn. Nếu xoài ngọt thì không cần thêm đường. Nếu xoài hơi chua thì bạn thêm đường tùy theo khẩu vị.\r\n\r\n2. Bột báng cho vào nồi nước nấu đến khi bột trong.\r\n\r\n3. Đổ bột báng và xoài vào chung một cái bát to rồi cho vào tủ lạnh khoảng 2 giờ cho mát.\r\n \r\n\r\n4. Khi ăn đổ xoài và bột báng vào cốc cho thêm ít nước cốt dừa và vài miếng xoài tươi cắt vào', 'Món tráng miệng rất ngon mà lại rất dễ làm. Thích hợp cho mùa hè nóng bức.', NULL, 'image/IMG_Douong/Che_XoaiThomMat.jpg', 4),
(14, 'Chè Khoai Tím', ' - Trân châu khô 1 gói  - Khoai lang tím 2 củ  - Sữa tươi  - Mật ong', ' Khoai lang gọt vỏ, cắt miếng vuông, sau đó hấp hoặc luộc chín.\r\n\r\n- Cho hạt trân châu khô vào đun sôi trong nước khoảng 8 phút, vừa đun vừa khuấy để không bị dính nồi. Đun đến khi trân châu nở ra và trở nên trong suốt là được.\r\n Khi trân châu chín cho ra ngâm luôn vào nước lạnh để nguội nhanh và không bị dính.\r\n\r\n- Xếp khoai vào bát, thêm trân châu rồi đổ sữa vào, nếu bạn thích ngọt hơn nữa thì cho mật ong tùy theo khẩu vị.\r\n', 'Khoai lang tím rất tốt cho sức khỏe của các sĩ tử đang bước vào các kì thi quan trọng. Đặc biệt khi kết hợp với sữa và mật ong món chè này lại càng hấp dẫn và thơm ngon.', NULL, 'image/IMG_Douong/Che_KhoaiTim.jpg', 4),
(15, 'Chè Sắn', ' 0,5kg sắn tươi  0,5kg đường kính  0,02kg bột đao Nước hoa bưởi', '\r\nSắn tươi bóc vỏ rửa sạch.\r\n\r\nChuẩn bị một chậu nhỏ nước sạch. Dùng bàn nạo nạo sắn thành sợi rơi vào chậu nước.\r\n\r\nNạo xong vớt sắn để ra rổ cho ráo nước.\r\n\r\nNước còn lại trong chậu để lắng trong gạn bỏ nước lấy bột đặc.\r\n\r\nCho khoảng 1 lít nước vào nồi đun sôi, cho sắn vừa nạo và bột đặc vừa gạn vào, khuấy nhẹ tay cho sắn chín.\r\n\r\nChú ý điều chỉnh lửa vừa nhỏ để không bị bén nồi. Khi sắn chín trong mới cho đường vào.\r\n\r\nHòa bột đao với một chút nước lã, cho từ từ vào nồi chè khuấy đều tay.\r\n\r\nKhi nồi chè sôi lại và chín trong bắc ra cho nước hoa bưởi vào.', 'Không có cảm giác ngọt hắc như các loại chè khác, chè sắn có vị bùi béo, và hương thơm đặc trưng do ướp cùng hoa bưởi. Cách nấu chè sắn cũng khá đơn giản như những loại chè khác.', NULL, 'image/IMG_Douong/Che_San.jpg', 4),
(16, 'Chè Xoài Trân Châu', '- ½ chén trân châu khô (loại này có thể mua ở chợ, tại các quầy hàng khô, hàng tạp hóa)  -2 quả xoài chín  * 3 cốc nước  -3 thìa đường đỏ  -1 hộp cốt dừa  -¼ thìa muối  Ngoài nguyên liệu chính là xoài bạn hãy chọn thêm một loại quả nữa thể theo sở thích, có thể là: chuối, đu đủ, kiwi hay khế…', 'Lấy ½ chén hạt trân châu khô, ngâm nước cho ngấm trong 15 – 20 phút sau đó đổ nước đi để ráo và cho vào trong nồi.\r\n\r\nCho 2 chén nước và ¼ thìa muối vào nồi trân châu. Bật bếp đun sôi rồi nhỏ lửa, khoấy nhẹ để các hạt không bị dính vào nhau. Đến khi thấy các hạt trở nên trong suốt là được. Không nên đun lâu quá, trân châu sẽ nát.\r\n\r\nĐổ nước đi và cho hạt trân châu ra bát để nguội\r\n\r\nXoài chín gọt vỏ\r\n\r\nThái miếng và cho vào máy xay sinh tố.\r\n\r\nCho 3 thìa đường đỏ và ¼ chén nước vào máy xay cùng, xay đến khi hỗn hợp nước, xoài và đường mịn và sánh. Ở bước này bạn có thể nếm xem độ ngọt đã vừa chưa, thêm đường nếu bạn muốn. Còn nếu đặc quá bạn có thể thêm nước và tiếp tục xay.\r\n\r\nKhi hỗn hợp đã đạt yêu cầu, cho xoài xay ra các cốc to, hoặc bát.\r\n\r\nCắt miếng một quả xoài khác, cắt thêm một ít chuối hoặc vải (hoặc bất cứ loại trái cây nào mà bạn thích: đu đủ, kiwi…)\r\n\r\nChia đều phần trân châu đã đun chín lúc nãy vào bát xoài (lúc này trân châu hơi dính và đặc lại)\r\n\r\nThêm các loại quả khác lên trên. Rót nước cốt dừa lên trên cùng\r\n\r\nĂn luôn hoặc thêm đá bào và thưởng thức\r\n\r\nĂn kèm với kem và thạch nữa rất ngon\r\n\r\nVị trái cây quyện với vị cốt dừa thơm ngậy\r\n\r\nNếu không muốn thêm đá, bạn trộn đều xoài, cốt dừa và trân châu, đặt vài lát hoa quả lên trên rồi cho vào tủ lạnh\r\n\r\nThêm tào phớ vào ăn rất lạ mà ngon\r\n\r\nCho vào các cốc nhựa đậy nắp, mang theo ăn tráng miệng sau bữa cơm trưa văn phòng', 'Kiểu chè này không cần phải nấu nướng nhiều, mùi vị thơm ngon và rất đẹp mắt, chè xoài trân châu sẽ làm mùa hè của bạn thêm thú vị.', NULL, 'image/IMG_Douong/Che_XoaiTranChau.jpg', 4),
(17, 'Chè Bí Đỏ Thơm Ngon', 'Bí đỏ  Đường  Bột sắn', 'Bí đỏ cắt thành miếng nhỏ vừa ăn, đun với nước tầm 15 phút cho chín mềm. KHi bí vủa chín tới nêm dường vừa ăn. Chú ý, bí đỏ có vị ngọt sẵn vì thế không nên cho quá nhiều đường món chè sẽ ngấy, đánh mất vị ngọt tự nhiên của bí.\r\n\r\nPha bột sắn với nước lạnh. Bí chín quấy bột sắn vào cùng đảo đều tay\r\n\r\nMúc ra bát. Có thể dùng nóng hoặc để lạnh ăn cũng rất tuyệt', 'Có rất nhiều cách chế biến món chè bí đỏ như nấu cùng đậu xanh, hạt sen...tuy nhiên nếu bạn chỉ yêu thích hương vị ngọt thanh của bí đỏ bạn hoàn toàn có thể nấu chè bí đỏ nguyên chất. Món này không chỉ hấp dẫn, ngon miệng mà còn rất tốt cho sức khỏe đặc biệt là làn da phụ nữ. ', NULL, 'image/IMG_Douong/Che_BiDo.jpg', 4),
(18, 'Chè Đậu Đỏ Hạt Sen ', '100 gr đậu đỏ  100 gr hạt sen  Vỏ quýt khô  200 gr đường hoa mai', 'Đậu đỏ rửa sạch, loại bỏ những hạt mốc, hỏng, cho vào nồi cùng nước, đun khoảng 30 - 40 phút cho nhừ.\r\n\r\nHạt sen bỏ tâm, rửa sạch, cho vào ninh cùng đậu đỏ cho mềm.\r\n\r\nCho vỏ quýt thái chỉ, đường hoa mai vào đun cùng khoảng 20 phút cho đậu và hạt sen thấm vị ngọt.\r\n\r\nBắc ra, để nguội và thưởng thức.', 'Món chè đậu đỏ hạt sen vừa ngon miệng lại vừa mát, có tác dụng rất tốt đối với sức khỏe. Các bà nội trợ hãy thử trổ tài nấu món chè này cho gia đình mình nhé!', NULL, 'image/IMG_Douong/Che_DauDoHatSen.jpg', 4),
(19, 'Chè Ngô Thơm Mát', '      * 3 chén nước     * 2 chén đường     * ¼ chén hạt trân châu khô     * 4 bắp ngô     * Nước cốt dừa     * Nửa chén bột sắn', 'Ngô rửa sạch, cho vào nồi đổ nước vừa ngập và luộc chín.\r\n\r\nKhi ngô chín, vớt ra để nguội.\r\n \r\nBắp ngô nguội, dùng dao cắt lấy phần hạt ngô. \r\nPhần nước ngô đem lọc qua một cái rây để loại bỏ hết râu ngô và các vảy ngô còn sót lại rồi lại cho lên bếp đun sôi. Cho phần hạt ngô đã cắt ra từ nãy rồi cho đường vào vừa ngọt theo sở thích. \r\n\r\nĐun sôi chè ngô trở lại và vặn nhỏ lửa. Hòa tan bột sắn trong nước lạnh và từ từ cho vào nồi, vừa cho vừa khuấy đều. Độ đặc của chè là do bạn, nếu thích loãng hơn có thể cho bớt bột. \r\nChuẩn bị hạt trân châu. Cho 2 chén nước vào nồi đun sôi. Cho 2 thìa to trân châu khô vào đun. \r\nĐun cho đến khi hạt trân châu trở nên gần trong suốt thì đổ ra một cái rổ lọc, phần nước bỏ đi. Không nên đun trân châu quá lâu, nó sẽ bị nát và khi ăn không cảm thấy độ dẻo.\r\n \r\nSau khi trân châu vừa chín thì cho vào nồi chè, nếm lại độ ngọt của chè, đun sôi lại rồi tắt bếp.\r\nChè ngô có thể ăn nóng hoặc lạnh\r\nThêm đá, nước cốt dừa hoặc dừa tươi ăn ngon tuyệt\r\nNhững hạt trân châu nhỏ trong suốt tạo vị lạ cho chè ngô\r\n \r\nTrộn đều đá và nước cốt dừa trong chén chè\r\nHoặc để riêng cốt dừa và cho lên trên ', 'Vị ngọt bùi của ngô nếp mới vừa quen vừa lạ khi nấu cùng hạt trân châu dẻo thơm. Đây là món thơm bùi, mát ngọt và rất bổ dưỡng.', NULL, 'image/IMG_Douong/che_Ngo.jpg', 4),
(20, 'Chè Đậu Đen Hương Nhài ', '  300 gr đậu đen  Một nắm hoa nhài  200 gr đường cát  Một ống vani.', 'Đậu đen vo kỹ, ngâm nước lạnh khoảng 20 phút. Vớt ra, bắc lên bếp nấu mềm. Khi đậu đã mềm, cho đường vào nấu tan, rắc vani vào, tắt bếp.\r\n\r\nHoa nhài rửa sạch dưới vòi nước mạnh, cho vào nồi chè, đậy nắp nồi lại, hãm 10 phút cho hương nhài quyện vào hết trong chè.\r\n\r\nMúc chè ra ly, có thể dùng nóng hoặc dùng lạnh đều ngon.', 'Cái nóng mùa hè sẽ được xoa dịu ngay lập tức với một cốc chè đậu đen thơm dịu hương hoa nhài. Đây là món ăn đơn giản và dễ nấu.  ', 15000, 'image/IMG_Douong/Douong20.jpg', 4),
(21, ' Chè Hạt Sen Táo Đỏ ', '2 xâu hạt sen khô hoặc tươi,  200gr táo đỏ (loại có hột),  300gr đường phèn,  2 ống vani.', 'Hạt sen rửa sạch, ngâm nước 20 phút, cho vào xoong đổ nước ngập, đun sôi rồi hạ nhỏ lửa cho đến khi chín mềm.\r\n\r\nTáo đỏ rửa sạch bằng nước ấm, cho vào xoong luộc 5 phút, vớt ra rổ, để ráo nước.\r\n\r\nNấu tan đường phèn với nửa lít nước, lọc qua xoong khác, cho hạt sen, táo đổ vào nấu trên lửa nhỏ độ 10 phút, nhấc xoong xuống, cho vani vào khuấy đều. Múc chè ra chén hoạc ly. Dùng lạnh với đá đập nhỏ hoặc ăn nóng đều ngon.', 'Không cầu kỳ, không tốn quá nhiều thời gian, chè hạt sen táo đỏ là món ăn mát bổ, rất đáng để xuất hiện trong thực đơn ngày hè của gia đình bạn. ', NULL, 'image/IMG_Douong/Che_HatSenTaoDo.jpg', 4),
(22, 'Chè Long Nhãn ', '- 50g long nhãn khô. - 50 g hạt sen tươi - 1 muỗng canh đường phèn - 500 ml nước', 'Ngâm nhãn vào nước ấm khoảng 30 p cho nhãn nở mềm\r\nSau đó ấn từng hạt sen vào trong quả nhãn.\r\nCho hạt sen và nhãn, đường vào nấu 15 phút.\r\nNhắc xuống để nguội, bỏ chút đá vào uống lạnh.\r\nMón này vừa mát vừa giúp ngủ ngon.', 'Nếu bạn hoặc người thân đang mất ngủ hoặc bị stress thì bát chè long nhãn thơm ngon sẽ là bài thuốc trị bệnh tuyệt vời. Chắc hẳn bạn sẽ nhận được vô số lời khen sau khi chế biến thành công món chè ngon miệng này.  ', 25000, 'image/IMG_Douong/Douong22.jpg', 3),
(23, 'Chè Bắp Sữa', '4 cái ngô nếp  200gr dừa nạo  300gr đường  3 muỗng canh bột năng', 'Bắp lột vỏ, bào mỏng hạt. Cho lõi bắp, rau bắp vào nồi nước lạnh đun lấy nước ngọt. Vớt bỏ lõi và rau bắp.\r\n\r\nLấy một chén con nước bắp luộc cho vào tô dừa nạo, vắt lấy nước cốt\r\n\r\nNêm một muỗng canh bột năng và muối vào cốt dừa, bắc lên bếp đun sôi cho đến khi sánh đều.\r\n\r\nCho bắp đã bào vào nồi nước luộc ngô cùng với lá dừa đun nhỏ lửa gần chin thì vớt bỏ lá dừa.\r\n\r\nMúc một ít chè bắp vào tô dừa nạo vắt lấy nước ráo. Cho nước dừa vào nồi cùng với đường và bộ năng đã hòa tan đun cho tới khi chè chin mềm\r\n\r\nNêm va-ni, múc chè ra chén con, khi ăn rưới nước cốt dừa lên trên.', 'Chè bắp thơm ngọt, không phải cái ngọt của đường mà là sự ngọt riêng có của sữa bắp non, cái ngọt của mạch nguồn đất bãi sa bồi.', NULL, 'image/IMG_Douong/Che_BapSua.jpg', 4),
(24, 'Chè Đậu Ngự ', '300 gr đậu ngự  150 - 200 gr đường  Vanilla', 'Đun nước sôi, tắt bếp, cho đậu vào đậy vung, để một lúc rồi đổ ra một chậu nước lạnh. Bóc vỏ đậu cho sạch.\r\n\r\nĐậu được đem hấp chín nhưng không được làm nát.\r\n\r\nCho nước vào nồi, cho đường vào khuấy tan, cho đậu vào đun một lúc cho thấm đường, cho vanilla, bắc ra.\r\n\r\nMúc chè ra từng ly, cẩn thận để không làm nát đậu, có thể bỏ thêm hoa nhài.', 'Món chè đậu ngự vừa có thể giải khát, vừa có tác dụng bồi bổ cơ thể, là một món ăn rất tốt trong mùa hè.     ', 15000, 'image/IMG_Douong/Douong24.jpg', 4),
(25, 'Chè Hoa Cúc Nấm Tuyết ', 'Hoa cúc 10g  Kỷ tử 20g  Nấm tuyết 50g  Táo đỏ 50g  Hạt sen 50g  Đường phèn vừa đủ', 'Hoa cúc rửa sạch vắt ráo nước\r\n\r\nNấm tuyết và hạt sen ngâm cho nở, sau đó rửa sạch nấm tuyết cắt bỏ chân nấm và xắt nhỏ.\r\n\r\nTáo đỏ nếu muốn cho hạt táo nở mềm, trước khi nấu nên hấp táo khoảng 5 phút.\r\n\r\nCho nước vừa đủ vào nồi, nước sôi cho hạt sen, táo đỏ và nấm tuyết vào nấu, khi hạt sen mềm cho tiếp hạt kỷ tử và đường phèn vào, nấu sôi khi đường tan nấu thêm 1-2 phút nửa thì cho tiếp hoa cúc, khi chè sôi lại tắt bếp đậy nắp khoảng 5 phút để hoa cúc ra mùi thì vớt ra, bỏ đi phần xác hoa, món chè này ăn nóng hay lạnh đều ngon.', 'Món chè hoa cúc nấm tuyết ăn mát, thanh nhuận, rất thích hợp cho mùa hè, món ăn này dùng nóng hay lạnh đều ngon và cách nấu cũng rất đơn giản.', NULL, 'image/IMG_Douong/Che_HoaCucNamTuyet.jpg', 4),
(26, 'Kem Kiwi', '- Kiwi 3 – 4 quả - Đường - Nước - Nước ép Nha đam (mua sẵn ở siêu thị, phần này tùy chọn thêm, có thể không cần cho)', '- Quả kiwi thịt mềm, vị ngọt dịu. Kiwi gọt vỏ, thái nhỏ.\r\n- Cho kiwi và đường vào máy xay sinh tố, lượng đường khoảng 5 thìa, tùy khẩu vị ngọt của bạn mà có thể thêm bớt. Thêm 100 ml nước lọc.\r\n\r\n- Nhấn nút xay trong 30 giây.\r\n\r\n- Vì kiwi rất mềm nên xay nhanh, không cần xay kỹ quá. Kiwi còn giữ lại chút thịt quả và nguyên hạt sẽ đẹp mắt hơn.\r\n\r\n- Có thể cho thêm ½ ly nước ép nha đam, sẽ làm tăng thêm vị tươi mới, thanh mát cho món tráng miệng.\r\n- Đổ hỗn hợp ra một cái khay hoặc hộp. Cho vào ngăn đá tủ lạnh, cứ 30 - 40 phút kiểm tra một lần rồi lấy thìa trộn thật kỹ, sau đó lại đặt trở lại tủ lạnh. Làm như vậy trong khoảng 3 – 4 giờ, đến khi kem đông lại. \r\n- Thật ra làm kem thế này sẽ có các tinh thể nước đá đóng lại, không mịn như kem thường bởi mình không cho kem tươi hay sữa trứng gì cả lại không xay nhuyễn kiwi nữa, đây đơn thuần là kiwi xay đông lạnh nhưng mình thấy ăn cũng ngon, đặc biệt rất mát.\r\n- Nếu bạn “sốt ruột” và ngại làm kem, phải trộn và kiểm tra thường xuyên thì có một cách hay đây. Chỉ cần xay nhuyễn kiwi với nước ép nha đam, thêm đường và đá thế là ly sinh tố xanh mướt thơm mát đã sẵn sàng rồi.\r\n\r\nVậy bạn còn chờ gì nữa, hãy chọn cho mình một món tráng miệng ngon từ quả kiwi hấp dẫn nhé.\r\n\r\n\r\nMách nhỏ:\r\n\r\nKiwi chọn quả không dập, có lông màu trắng bạc là quả tươi.', 'Hãy lấy lại năng lượng, tăng thêm sảng khoái với kem và nước uống từ trái Kiwi diệu kỳ.  Kiwi màu xanh tạo cảm giác tươi mới và mát mẻ, thứ quả này lại giàu vitamin C, chất xơ, tốt cho tiêu hóa, chống lão hóa cho làn da luôn trẻ đẹp. Kiwi tốt cho cả trẻ nhỏ và người lớn.', NULL, 'image/IMG_Douong/Kem_Kiwi.jpg', 5),
(27, 'Kem Chiên Chocolate ', '- 100ml kem tươi.  - 50g bơ.  - 200g chocolate.  - 125 g bột mì.  - 1 quả trứng gà.  - 25 g bột ca cao.  - 50ml sâm -panh.  - Muối, đường.', '- Kem nấu sôi, làm nóng chảy bơ và  chocolate , trút vào kem nấu sôi khuấy đều.\r\n\r\n- Làm lạnh hỗn hợp rồi nặn thành từng viên tròn, để tủ lạnh, khi nào chuẩn bị dùng mới lấy ra chế biến.\r\n\r\n- Rây mịn bột mì và ca cao, thêm muối và đường rồi đập trứng vào, trộn kỹ. Thêm sâm -panh vào cho hỗn hợp có độ loãng vừa đủ để nhúng Sô-cô-la vào.\r\n\r\n- Lấy viên chocolate đã làm lạnh ra, nhúng vào hỗn hợp và đem chiên ngập dầu ở nhiệt độ 180 độ, vớt ra để ráo dầu.\r\nTrình bày:\r\n\r\nXếp chocolate chiên ở một góc. Ở giữa xếp một trái dâu tây cắt lát và một nhánh bạc hà. Đầu còn lại xếp một viên kem dâu và nhánh đường kính để trang trí.', 'Hãy thử chế biến món kem chiên chocolate này, chắc chắn bạn sẽ đem lại nhiều cảm giác thú vị cho người thưởng thức.', NULL, 'image/IMG_Douong/Kem_ChienChocolate.jpg', 5),
(28, 'Kem Xốp Socola', 'Socola đen: 150g Lòng trắng trứng gà: 100g Đường bột: 200g Kem tươi: 500ml', 'Cho kem tưới và 0,1 kg đường vào trng một âu inốc sạc đánh cứng để riêng đánh trên một chậu đá hoặc kem tươi để thật lạnh trước khi đánh)\r\n\r\nSocola đun chảy (cách thuỷ với nhiệt độ 32 độ C), Phần đường còn lại đun thành siro với 1 chút nước.\r\n\r\nLòng trắng trứng đánh cứng như tuyết sau đó cho đường đun (đường đang sôi) ở nhiệt độ trên vào và đánh đều liên tục(chú ý không để đường đông lại).\r\n\r\nTrộn một chút kem tươi với socola còn ấm dùng phới đánh đều sau đó trộn với kem còn lại, cuối cùng trộn với lòng trắng trứng đã được đánh ở trên.\r\nBảo quản trong tủ lạnh, khi bày kem ra từng cốc. Trang trí bằng kem tươi đã  được đánh ', 'Vào những ngày đầu thu oi bức này thì còn gì tuyệt vời hơn là được ăn một ly kem do chính tay mình làm', NULL, 'image/IMG_Douong/Kem_XopChocolate.jpg', 5),
(29, 'Kem Dâu Tình Yêu', '400 g dâu tây (chọn quả tươi, chín đỏ), 1 bát đường xay, 2 lòng đỏ trứng gà, 500 ml kem tươi', '- Dâu tây rửa sạch nhiều lần với nước muối pha loãng, cắt nhỏ rồi cho vào máy sinh tố xay nhuyễn.\r\n\r\n- Dùng máy đánh trứng đánh nổi lòng đỏ trứng, rồi cho từ từ đường vào và tiếp tục đánh cho tan đường, cho tiếp phần dâu đã xay vào đánh đều lên.\r\n\r\n- Cho tiếp kem tươi vào và tiếp tục đánh cho đến khi hỗn hợp này đều và nổi mịn, hoặc có thể đánh kem tươi riêng cho nổi, rồi trộn đều với hỗn hợp dâu trứng đường ở trên.\r\n- Cho kem vào khuôn rồi cho vào tủ đá.\r\n\r\n- Dùng muỗng, múc kem ra ly. Trang trí thêm bánh và dâu tây tươi cắt hạt lựu.', 'Bạn băn khoăn không biết làm gì cho một nửa của mình trong mùa hè oi bức này. Tại sao không thử tự tay chế biến món kem dâu tình yêu này, chắc chắn người ấy sẽ bất ngờ với ly kem dâu mát lạnh, ngọt ngào như chính tình yêu của bạn. ', NULL, 'image/IMG_Douong/Kem_DauTinhYeu.jpg', 5),
(30, 'Kem Chanh Cho Ngày Nắng', '1/2 cốc nước  1/4 cốc đường  Vỏ chanh  1/2 cốc nước chanh vắt  Rượu vodka hương chanh', 'Cho nước, đường, vỏ cam nạo vào nồi. Bắt lên bếp, để lửa vừa. Thỉnh thoảng khuấy đều cho đến khi đường tan, và nồi sôi lên.\r\n\r\nNhấc nồi ra khỏi bếp, đậy nồi lại, để yên khoảng 20~40 phút. Rồi chuyển hỗn hợp vào tô. Cho nước chanh vào, trộn đều.\r\n\r\nĐể vào tủ lạnh ướp cho đến khi lạnh, khoảng 1~2 tiếng.\r\n\r\nTrước khi cho vào máy làm kem, nếm thử, nếu cần thêm đường thì cho thêm vào, khuấy đều khoảng 2~3 phút cho đến khi đường tan hẳn.\r\n\r\nKhi hỗn hợp lạnh rồi. Cho hỗn hợp vào hộp trữ kem rộng có đáy cạn, đậy kín lại. Bỏ vào ngăn đá khoảng 5~6 tiếng hoặc cho đến khi kem đông lại. Cho vào máy xay thức ăn, hoặc dùng đồ đánh trứng, đánh để làm vỡ đá đông lại trong kem, cho kem trộn đều và trở nên mịn. Cho vào trở lại tủ đông. Lập lại như vậy khoảng 2 lần.\r\n\r\nMúc ra ly hoặc cốc trang trí.', 'Là món đã quá quen thuộc với mùa hè song hương vị của mỗi một loại kem lại có sức hấp dẫn riêng. Từ những nguyên liệu và cách pha chế khác cũng tạo ra những mùi vị khác nhau. Kem chanh thoảng chua, đọng lại vị ngọt nơi cổ chắc chắn sẽ đánh thức cảm giác của bạn.', NULL, 'image/IMG_Douong/Kem_ChanhNgayNang.jpg', 5),
(31, ' Kem Tuyết Chanh Dây ', '- 2 lòng trắng trứng gà  - 200gr đường trắng  - 100ml nước  - 200 gr sữa bột béo  - 200ml nước ép chanh dây (hoặc dùng chanh dây tươi bỏ hột)  - 2 ống vani  - Tô thủy tinh', '- Cho đường và nước, vani vô nồi nấu lửa riu riu 15 phút cho đường tan hết và hơi sánh lại\r\n\r\n- Dùng máy đánh trứng đánh bông lòng trắng trứng 7 phút\r\n\r\n- Đổ từ từ nước đường còn đang nóng vào, đánh thêm 5 phút nữa, đổ ra tô, bỏ tủ lạnh 20 phút\r\n\r\n- Xay hỗn hợp nước ép chanh dây và sữa bột béo\r\n\r\n- Dùng muỗng to trộn với hỗn hợp nước đường và trứng đã lạnh cho đều\r\n\r\n- Để ngăn đá tủ lạnh 1 tiếng là ăn được\r\n\r\nMón kem này còn có tác dụng an thần, trị chứng mất ngủ, thích hợp ăn vào buổi tối đó bạn', 'Bạn đang kiếm một thức uống mát lạnh, đương đầu với sức nóng của mùa hè! Thì đây: Kem tuyết chanh - Xin mời!   ', 15000, 'image/IMG_Douong/Douong31.jpg', 5),
(32, 'Kem Việt Quất ', '2 tách nam việt quất  3/4 tách đường  1/8 thìa cà phê muối  1 tách sữa tươi   1,5 tách kem béo  lá bạc hà.', 'Bước 1:\r\n\r\nNam việt quất rửa sạch, cho vào nồi cùng đường và muối, nấu ở nhiệt độ trung bình,dùng nĩa vừa khuấy vừa xăm cho mềm.\r\n\r\nBước 2:\r\n\r\nThỉnh thoảng khuấy đều, nấu lửa vừa trong khoảng 5 phút rồi tắt bếp để nguội\r\n\r\nBước 3:\r\n\r\nCho nam việt quất đã nấu vào máy xay, thêm sữa tươi vào, bật máy đánh đến khi mịn rồi khuấy kem vào. Rây hỗn hợp vào tô, dùng lưng muỗng ép để chắt hết nước.\r\n\r\nBước 4:\r\n\r\nBọc lại, để lạnh ít nhất 2 tiếng hoặc đến khi lạnh\r\n\r\nBước 5:\r\n\r\nDùng máy làm kem, làm đông hỗn hợp lại.\r\n\r\nBước 6:\r\n\r\nCho vào tủ lạnh để làm cứng kem lại.\r\n\r\nBước 7:\r\n\r\nDọn dùng trong ly martini, dùng trái tươi và bạc hà trang trí.\r\n\r\nThêm một thanh sô cô la cho đẹp.\r\n\r\nMách nhỏ: Tuy có thể làm nhanh nhưng tốt hơn nên để kem trong tủ lạnh 1 ngày trở lên, như thế kem sẽ cứng và ngon hơn. Muối chỉ cho ít đủ để tạo độ cứng cho kem.', 'Mùa hè thích nhất vị kem mát lạnh! Thêm vị kem mát vào trong thực đơn ngày hôm nay như một điều bất ngờ dành cho gia đình.  ', 10000, 'image/IMG_Douong/Douong32.jpg', 5),
(33, 'Kem Chocolate', '500 ml sữa tươi  4 lòng đỏ trứng gà  2 thanh chocolate  100 gr đường  1 hộp kem tươi', 'Chocolate đun nóng chảy.\r\n\r\nKem tươi đánh nổi.\r\n\r\nĐánh trứng với đường cho bông.\r\n\r\nCho sữa tươi và chocolate vào nồi đun, khuấy đều tay và liên tục. Khi hỗn hợp sôi, bạn rót sang bát trứng đã đánh, dùng dụng cụ đánh trứng khuấy cho tan kem sữa.\r\n\r\nCho hỗn hợp trên vào nồi đun nhỏ lửa, quấy thật đều tay cho đến khi kem nặng tay thì bỏ ra.\r\n\r\nĐể kem nguội hẳn, cho kem tươi vào đảo đều.\r\n\r\nBỏ vào hộp để tủ lạnh.', 'Món kem chocolate ngọt ngào sẽ khiến bạn vô cùng thích thú. Nó sẽ giúp bạn xua tan cái nắng nóng mùa hè  ', 6000, 'image/IMG_Douong/Douong33.jpg', 5),
(34, 'Kem Xoài', '- 2 chén nước sinh tố xoài  - 4 lòng đỏ trứng gà  - 2/3 chén đường  - 1 thìa nước chanh  - ½ chén nước cốt dừa  - ½ chén kem tươi  - Vỏ quế, xoài tươi, dừa tươi, dừa nạo khô', 'Cho trứng, đường vào tô, dùng máy đánh trứng đánh nhuyễn cho đường tan hết. Đặt bát trứng lớn chảo nước nóng đun nhỏ lửa, dùng máy đánh trứng đánh tiếp cho đến khi hỗn hợp sệt lại thì nhấc khỏi bếp, đánh thêm khoảng 1 phút nữa cho nguội hẳn.\r\nĐổ nước sinh tố xoài, nước cốt dừa và kem tươi vào, dùng thìa khuấy đều, nhẹ tay.\r\n\r\nĐổ hỗn hợp kem vào khuôn kim loại cỡ 18x27 cm, đậy nắp lại và làm lạnh trong vòng 3 tiếng hoặc cho đến khi hỗn hợp kem lạnh hoàn toàn.\r\n\r\nChuyển kem trở lại tô, dùng máy đánh trứng ở tốc độ nhanh nhất đánh tiếp cho hỗn hợp nhuyễn, đặc lại như bột là được. Đổ kem vào khuôn kim loại làm lạnh trong 5 tiếng hay để qua đêm càng tốt.\r\n\r\nKhi ăn múc kem ra vỏ quế, cho vài lát xoài cắt mỏng, một ít dừa tươi, dừa khô nạo lên trên.', 'Xoài là thức ăn bổ não, rất tốt cho những người lao động trí óc, học sinh ôn thi. Một ly kem xoài thơm mát là sự biến tấu giúp bạn có được món tráng miệng tuyệt vời.  ', 10000, 'image/IMG_Douong/Douong34.jpg', 5),
(35, 'Kem Đào Mát Lạnh Ngày Hè ', 'Kem vani  Đào tươi  Đường  Siro vị đào  Bánh quế', 'Đào tươi tách bỏ hột, cắt múi cau ướp với đường.\r\n\r\nCho kem ra ly, rưới siro lên, xếp những miếng đào vào, cho thêm bánh quế và thưởng thức.', 'Món kem vani được làm mới bởi hương vị của những trái đào thơm mát. Những ly kem ngọt ngào, mát lạnh sẽ khiến ngày hè của bé thêm vui.   ', 15000, 'image/IMG_Douong/Douong35.jpg', 5),
(36, 'Caramel Cho Ngày Hè ', '10 quả trứng  250 g đường  1 lit sữa tươi  1 ống vani', 'Cho100 g đường cùng nước lạnh, đun sôi tới khi đường có màu vàng nâu. Đây chính là đường caramel, đổ ra cốc.\r\n\r\nTrộn 10 lòng đỏ, 4 lòng trắng và 150 g đường, đánh tan hỗn hợp trên rồi đổ sữa vào khuấy đều, lọc qua lưới lọc, sau đó đổ vào cốc đã có đường caramel.\r\n\r\nXếp cốc vào chõ hấp chín. ', 'Caramel vừa ngọt vừa mát lại có mùi thơm của trứng, sữa rất hợp với mùa hè nắng nóng. Caramel không chỉ ngon miệng mà còn rất bổ dưỡng nữa.     ', 15000, 'image/IMG_Douong/Douong36.jpg', 5),
(37, 'Kem Cà Phê Sữa ', '- 300ml cà phê pha sữa đặc (nhiều sữa và cà phê, ít nước). - 2 quả chanh. - 350ml nước đường giữ lạnh (nấu tan 500ml nước với 500gr đường, giảm lửa liu riu trong 5 phút, để nguội). - 1 lòng trắng trứng đành nổi xốp.', '- Trộn nước đường lạnh với cà phê sữa.\r\n- Vắt nước cốt chanh và hỗn hợp vào hỗn hợp cà phê sữa và nước đường.\r\n- Rót vào khuôn, đặt vào ngăn đá tủ lạnh.\r\n- Kem đóng váng, lấy ra đánh đều.\r\n- Thực hiện 2 lần, cách nhau 1 giờ, đến khi kem đặc.\r\n- Trộn thêm vào kem 1 lòng trắng trứng gà đánh nổi xốp rồi cho vào tủ đông.\r\n\r\n ', 'Dùng lạnh.', NULL, NULL, 5),
(38, 'Kem Nho ', '- 1/2kg nho tươi - 2 quả chanh - 150g đường - 100ml nước - 200ml kem tươi - Một ít kẹo màu', 'Bóc sạch vỏ nho, thái nhỏ, gọt vỏ chanh, thái sợi nhuyễn, phần ruột chanh vắt nước cốt. Đun sôi đường với nước khoảng 5 phút. Cho nước cốt chanh, vỏ chanh vào, giữ lạnh. Cho hỗn hợp nước cốt chanh vào máy đánh kem, đánh trong 12 phút. Tiếp đến cho kem tươi vào đến khi đặc mịn, cho nho vào trộn đều, giữ lạnh.\r\nDùng thìa tròn, múc từng viên kem ra ly hay đĩa, trang trí thêm vài quả nho, rắc thêm kẹo màu. ', 'Món kem này dễ làm, ăn rất ngon, lạ miệng.', NULL, NULL, 5),
(39, 'Kem Thuyền Hoa ', '- Kem các loại. - 1/2 trái dứa. - Siro. - Kem sữa tươi. - Trái dâu tây. - Nho. - Lá dứa. - 1 chiếc ô nhỏ', '- Dùng 1/2 trái dứa, lấy 2/3 ruột, cho kem vào trong.\r\n- Trang trí một chút trái nho, dâu tây, dù, sữa đặc và siro.\r\n- Khi dọn ra đặt trên đĩa bằng.', 'Là loại kem rất ngon có vị ngọt và béo.', NULL, NULL, 5),
(40, 'Kem Yogurt', '2 hũ yogurt, 1/3 trái xoài, 1/2 trái kiwi, 2 trái dâu tây, kẹo màu, sô-cô-la, nước cốt chanh, 2 viên đá tinh khiết', 'Đá tinh khiết cho vào máy xay thật nhuyễn, đổ vào ly. Cho một lớp yogurt sô-cô-la trên mặt ly. Sau đó rưới một lớp si rô có mùi bạn yêu thích lên trên và trang trí bằng các loại trái cây nhiều màu như nho, xoài, đu đủ, táo... và các loại kẹo dẻo, kẹo màu, một chút sữa đặc. Cuối cùng rắc thêm vài hạt cốm màu lên trên.', 'Yogurt là món ăn dễ làm và bổ dưỡng, chỉ mất 5 phút bạn sẽ làm cho bạn bè phải tròn mắt ngạc nhiên về tài khéo léo của mình. ', NULL, NULL, 5),
(41, 'Sinh Tố Bơ Sữa ', '- 2 quả bơ sáp chin. (nhớ là bơ phải chin thì mới ngon. Lúc chọn bơ, bạn nêm bóp nhẹ xung quanh quả bơ, nếu nó đàn hồi lại thì là bơ chin, ngon. Hoặc có thể cầm quả bơ lên lắc lắc, nếu nghe thấy tiếng kêu nhẹ của hột đã lóc ra thì là bơ ngon). - 100ml sữa tươi - 30gr sữa đặc có đường - 30ml nước cốt dừa - Đá bào - 1 trái chanh - 40ml nước sôi để nguội - Đường', '- Cắt bơ ra, bỏ hột, bỏ vỏ, cắt miếng nhỏ rồi cho vào máy xay sinh tố\r\n- Cho thêm đường vào cối, thêm một chút nước sôi để nguội,\r\n- Cho sữa tươi vào máy xay sinh tố, cho vào bằng khoảng 2/3 lượng bơ.\r\n- Cho tiếp nước cốt dừa vào máy xay sinh tố, cho vào khoảng bằng 1/2 lượng bơ.\r\n- Sau đó cho sữa đặc có đường vào, cho vào khoảng 1/3 lượng bơ.\r\n- Thêm vào một muỗng nước cốt chanh\r\n- Xay nhuyễn hỗn hợp trên, sau đó cho đá bào vào, xay tiếp đến khi nhuyễn ra thì thôi\r\n\r\n- Rót vào ly, và thế là chúng ta đã có một món sinh tố bơ thật là mát lạnh, thơm ngon và bổ dưỡng.', 'Sẽ thật tuyệt vời khi được thưởng thức một ly sinh tố bơ mát lạnh giữa mùa hè oi bức này. Có rất nhiều cách để pha chế được một ly sinh tố bơ, nhưng cách pha chế sau đây đảm bảo sẽ mang lại cho bạn một món sinh tố mát lạnh, đâm đà, thơm ngon.', NULL, NULL, 6),
(42, 'Sinh Tố Sapôchê ', '- Sapôchê : 1quả (100g)  - Sữa tươi : 100 ml  - Đường : 10g  - Đá đập hay bào nhuyễn.', '- Sapôchê gọt vỏ, bỏ hột, cắt miếng.\r\n\r\n- Cho Sapôchê, Sữa tươi, đường vào máy xay sinh tố, xay nhuyễn.\r\n\r\n- Cho ly sinh tố vào tủ lạnh (ngăn mát) để lạnh, hoặc có thể đập đá nhuyễn cho vào cối xay luôn với sinh tố\r\n\r\n- Trang trí thêm que khuấy có hình trái cây xinh xắn để ly sinh tố trông ngộ nghĩnh  và hấp dẫn  hơn.', 'Mùa hè nóng nực, được thưởng thức một ly sinh tố mát lạnh thật là tuyệt vời. Hãy thử pha chế món sinh tố Sapôchê theo cách dưới đây, chắc chắn cả gia đình bạn sẽ cảm thấy dễ chịu hơn trong cái nắng oi bức của mùa hè.', NULL, NULL, 6),
(43, 'Thơm Bùi Sinh Tố Chuối Lạc', '- Sữa tươi 500 ml  - Chuối chín 4 quả  - Lạc rang hoặc hạt điều 50g Đường hoặc mật ong', '\r\n- Lạc rang đập giập cho vào 1 cái bát. Lạc bạn có thể rang trên chảo rồi bóc vỏ, cũng có thể dùng lò nướng, lò vi sóng rang lạc.\r\n- Chuối bỏ vỏ cắt miếng, cho chuối, lạc, sữa vào máy xay sinh tố, có thể thêm đường, mật ong nếu muốn.\r\n- Nhấn nút xay trong 1 phút đến khi hỗn hợp nhuyễn mịn là được.\r\nMách nhỏ:\r\n\r\n- Chuối và sữa bạn có thể cho vào tủ lạnh trước, sau khi xay xong ly sinh tố sẽ có độ mát và bạn không cần cho thêm đá nữa.\r\n- Ngoài sữa tươi có thể dùng sữa đậu nành, hương vị cũng rất thơm ngon và thanh mát.', 'Hương vị thơm ngon vô cùng, một ly sinh tố bổ dưỡng thế này trong bữa sáng sẽ giúp bạn tràn đầy năng lượng và khởi đầu ngày mới thật ngọt ngào', NULL, NULL, 6),
(44, 'Sinh Tố Xoài Cà Chua', '- Cà chua bi 300g  - Xoài chín 1 quả  - Đường 3 thìa - Nước 200ml', '- Cho cà chua, xoài, nước và đường vào máy xay sinh tố.\r\n\r\n- Nhấn nút xay trong 30 giây, đến khi xoài và cà chua nhuyễn là được.\r\n- Rót ra các ly uống liền. Thêm đá nếu muốn uống lạnh', 'Trời nóng, trẻ con đến người lớn ai cũng thích các loại nước trái cây, sẵn có cà chua bi và xoài chín ngọt, mình thử chế biến thành sinh tố xem sao, và kết quả là ngon tuyệt vời.', NULL, NULL, 6);
INSERT INTO `douong` (`MaDU`, `TenDU`, `NguyenLieu`, `CachLam`, `GioiThieu`, `Dongia`, `Anh`, `MaDM`) VALUES
(45, 'Sữa Đậu Hạt Sen', ' - Hạt sen 50g - Đậu đỏ 50g - Đậu nành 50g - Đường, nước', ' Hạt sen, đậu đỏ và đậu nành đem ngâm nước trước 1 đêm. Hôm sau rửa sạch.\r\n Cho tất cả các loại hạt này vào máy xay sinh tố, thêm 1 lít nước, xay mịn rồi lọc bã bỏ đi.\r\n\r\n- Phần nước hạt sen và đậu cho lên bếp đun sôi, thêm nước nếu cần.  \r\n Khi uống có thể cho đường hoặc không tùy sở thích của bạn. \r\n\r\n- Thức uống này bổ dưỡng, hạt sen giúp thư giãn, an thần, tốt cho người hay làm việc căng thẳng. Đậu đỏ giàu chất sắt, tăng cường sức đề kháng, rất có lợi cho sức khỏe và vẻ đẹp phụ nữ. Còn đậu nành giàu protein, dinh dưỡng cao, chứa nhiều chất béo có lợi cho tim. ', 'Sữa đậu hạt sen có mùi thơm nhẹ, uống nóng hoặc lạnh đều ngon miệng, đặc biệt giàu năng lượng cho bữa sáng của bạn.', NULL, NULL, 6),
(46, 'Dâu Tây Chuối', '- 12 quả dâu tây tươi - 1 cốc sữa tươi - 1 quả chuối - 1 - 2 thìa mật ong Đá viên', '-  Dâu tây rửa sạch dưới vòi nước chảy, để thật khô. \r\n- Chuối bóc vỏ, thái miếng nhỏ\r\n- Cho tất cả các nguyên liệu vào máy xay sinh tố, nhấn nút xay trong 1 phút, đến khi các nguyên liệu nhuyễn mịn là được.\r\n- Rót ra các ly, trang trí thêm những lát dâu tây đỏ, uống liền. \r\n', 'Sinh tố chuối và dâu tây là một món mà người lớn trẻ nhỏ  đều thích.', NULL, NULL, 6),
(47, 'Sinh Tố Chanh Đu Đủ', '- Đu đủ chín nửa quả  - Chanh tươi nửa quả, chọn loại chanh vàng để vỏ đỡ bị đắng  - Mật ong  - Nước lọc', '- Đu đủ gọt vỏ, bỏ hạt, cắt miêng vuông\r\n\r\n- Chanh rửa sạch, cắt vài lát ngang, gạt bỏ hạt còn lại vắt lấy nước cốt. \r\n\r\n- Đu đủ đã thái miếng, 2 – 3 lát chanh cả vỏ, nước cốt chanh, mật ong 2 thìa (độ ngọt tùy khẩu vị của bạn), 1 cốc nước mát, tất cả cho vào máy xay sinh tố. Ấn nút xay khoảng 20 giây. Các nguyên liệu nhuyễn mịn và hòa quyện với nhau, đổ ra cốc. Thêm đá nếu bạn thích uống lạnh.', 'Sinh tố chanh đu đủ như một thứ “thần dược” bổ sung năng lượng, giúp bạn thư giãn, tỉnh táo, đặc biệt tốt khi dùng trong bữa sáng.', NULL, NULL, 6),
(48, 'Đu Đủ Sữa - Sinh Tố Sắc Đẹp', '- Đu đủ chín 200g - Sữa tươi 1 cốc - Mật ong hoặc đường', '- Đu đủ gọt vỏ, gạt bỏ hạt, thái miếng nhỏ.\r\n- Cho sữa, đu đủ và mật ong vào máy xay sinh tố. Xay đến khi hỗn hợp mịn nhuyễn là được.\r\n\r\n- Đổ ra các ly, thêm đá mát lạnh sẽ ngon hơn.', 'Làm việc không nghỉ ngơi, thời tiết thay đổi… khiến bạn mệt mỏi, da dẻ kém tươi trẻ, vậy hãy chọn ngay sinh tố đu đủ, thứ nước quả diệu kỳ cho sức khỏe và sắc đẹp của bạn.', NULL, NULL, 6),
(49, 'Sinh Tố Dứa', '  - Dứa (thơm) 1 quả  - Dưa vàng 1 miếng to, bạn có thể dùng dưa lê, dưa bở, dưa lưới... đều được.  - Đường  - Sữa tươi 1 cốc', '- Dứa gọt vỏ, bỏ mắt, cắt miếng mỏng\r\n\r\n- Dưa gọt vỏ, lấy phần thịt quả, cắt miếng vuông.\r\n\r\n- Cho hoa quả, sữa tươi và 1 thìa đường vào máy xay sinh tố, thêm đá viên. Nhấn nút xay trong 1 phút. Sinh tố nhuyễn mịn là được.', 'Thời tiết sắp chuyển hè oi nóng, nếu uống mỗi ngày một ly nước dứa tự làm như thế này thì không những xua tan được cái ngột ngạt mệt mỏi mà còn nuôi dưỡng vẻ đẹp, chống lão hóa rất hiệu quả.', NULL, NULL, 6),
(50, 'Sinh Tố Chuối Trà Xanh', '- 2 quả chuối  - 1 thìa nhỏ bột trà xanh Nhật Bản  - Sữa tươi 1 cốc hoặc 3 thìa sữa đặc  - Đường  -  Đá viên', '- Chuối chín bóc vỏ cắt miếng, cho chuối, sữa tươi, bột trà xanh, đá và lượng đường thích hợp vào máy xay sinh tố.\r\n- Nhấn nút xay trong một phút đến khi các nguyên liệu nhuyễn mịn là được. \r\nĐổ ra ly uống liền\r\n', 'Ly sinh tố thơm ngon lạ miệng giúp bạn tỉnh táo và sẵn sàng cho một ngày làm việc mới.  Chuối rất giàu vitamin và khoáng chất, giúp ngon miệng, loại bỏ stress đem lại tâm trạng vui vẻ… vậy bạn hãy làm cho mình và gia đình món sinh tố ngon bổ này nhé', NULL, NULL, 6),
(51, 'Nước Sen Dừa', '(cho 10 cốc): Hạt sen khô: 100 g, hoặc hạt sen tươi: 150 g, dừa thái nhỏ: 100 g, đường kính: 400 g, nước lọc. ', '- Hạt sen khô ngâm nước lã 2 tiếng cho nở, vớt ra xát, đãi sạch vỏ lụa.\r\n- Cho hạt sen vào nồi cùng với nước lã, đun sôi hớt bọt, kéo nồi ra cạnh bếp đun sôi âm ỉ khoảng 1 tiếng cho sen chín mềm, vớt sen ra để riêng. Nước luộc sen lọc sạch lấy nước trong để nguội.\r\n- Đường cho vào nồi cùng nước đun sôi lọc sạch, để nguội, đổ lẫn với nước luộc sen.\r\n- Khi uống cho sen, dừa vào cốc, rót nước đường vào 8/10 cốc, ướp lạnh hoặc cho đá vào uống lạnh.\r\nSau khi chế biến, hạt sen màu hơi vàng, nước trong trắng. Cốc nước nổi mùi thơm của sen, vị ngọt mát. Hạt sen chín bở, không bị vỡ chín. ', 'Vào ngày hè, bạn có thể tự chế biến các cốc nước gải khát với những nguyên liệu sẵn có. Nước sen dừa là một món uống có tính giải nhiệt cao và tốt cho sức khoẻ. ', NULL, NULL, 9),
(52, ' Nước Nha Đam Mát Lạnh ', '1 cốc nước  2 lá nha đam loại to  Nửa quả chanh tươi  Đường  Đá', 'Nước nha đam mát lạnh\r\nCắt lá nha đam thành những mẩu nhỏ\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nDùng dao cắt lọc phần vỏ bỏ đi, phần ruột bên trong như một thứ thạch trong suốt\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nLần lượt cắt và lọc hết vỏ để lấy phần trong suốt của lá\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nSau khi cắt thì bạn rửa sạch tay luôn vì lá nha đam rất nhớt\r\nVắt nước chanh tươi vào cốc nước, cho đường, ngoáy thật đều\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nCho nha đam vào một cái cốc nhỏ khác, dùng thìa dầm nhỏ ra, rồi cho vào cốc nước chanh\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nNếu bạn không thích dầm nha đam thì cắt miếng nhỏ ra rồi cho vào cốc nước chanh cũng được\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nThêm đá vào uống rất mát\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nChọn những viên đá hình trái tim hay bông hoa làm ly nước thêm sinh động\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nNgoài kiểu pha nước này thì có thể cho vào máy xay sinh tố, xay cùng với đường và nước chanh là bạn có một ly sinh tố rất ngon và thanh mát.\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nNha đam xay bông bọt và gần như tan hết thành nước\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nVị nha đam được giữ nguyên trong các miếng nhỏ trong suốt, ăn như thạch, rất thú vị\r\n\r\n \r\n\r\nNước nha đam mát lạnh\r\nThêm vài lát chanh tươi vừa để trang trí vừa để ly nước thêm thơm mát', 'Cây nha đam hay người ta vẫn gọi là cây lô hội, hoặc như trong các siêu thị lớn thì lại gọi là long tu, được biết đến như một nguyên liệu đặc biệt để sản xuất mỹ phẩm. Nhưng thật ra loại cây này còn có nhiều công dụng hơn thế.    ', 15000, 'image/IMG_Douong/Douong52.jpg', 9),
(53, 'Đón Hè Với Hương Vị Sấu', 'Sấu', 'Những hàng sấu là hình ảnh khá quen thuộc mà ta có thể bắt gặp khi đi dạo trên các con phố. Từng chùm sấu nhỏ hòa lẫn với màu xanh của cành, lá làm mát dịu biết bao ánh mắt của những người đi qua.\r\n\r\nNhìn vẻ ngoài sấu không đẹp, trái sấu nhỏ nhỏ, màu xanh và hơi chút xù xì. Khi nắng hè bắt đầu chói chang, thì đó là lúc những trái sấu xuất hiện nhiều ở chợ và các hàng quán. Từ những trái sấu nhỏ xinh ấy đã có vô vàn các món ăn ra đời, trong đó có một món không thể quên, đó là sấu ngâm.\r\nChế biến một lọ sấu ngâm không khó, nhưng cần có tính kiên nhẫn, tỉ mỉ. Gọt vỏ sấu rất khó vì trái nhỏ vỏ lại cứng. Gọt vỏ xong, dùng dao nhỏ và sắc gọt xoáy tách phần thịt sấu ra khỏi hột và ngâm chúng vào nước vôi loãng. Khoảng một giờ sau, vớt sấu ra, rửa sạch lại bằng nước lã, để ráo nước rồi chần nhanh sấu qua nước sôi. Sau đó, ngâm sấu với một lượng đường vừa đủ để nước sấu có vị ngọt thanh. Người ta còn cho thêm gừng giã dập để lọ sấu dầm tăng thêm hương vị.\r\nCó thể tùy vào thời tiết mà chúng ta có thời gian ngâm sấu phù hợp. Song tốt nhất là để sau 24 giờ vì khi đó sấu mới ngấm kỹ đường. Chắt một chút nước cốt, thêm vào vài ba quả sấu ngâm với vài muỗng đá bào là bạn có ngay một thứ nước giải khát dễ uống và khó quên.\r\n\r\nVới vị chua thanh thanh của sấu, pha lẫn vị ngọt của đường và mùi thơm của gừng, bạn sẽ cảm nhận được sự mát lạnh hòa tan trong miệng. Sấu giòn tuởng như vỡ vụn ra ngay khi vừa đưa vào miệng. Rồi cái vị ngòn ngọt, thơm thơm ấy cứ vương vấn mãi nơi cổ họng.\r\n\r\nKhông phải là thứ thức uống cầu kỳ, ngược lại sấu dầm rẻ tiền và dễ làm. Với một chút khéo léo của mình, những ly nước sấu mát lạnh tự tay bạn chế biến đã khiến ngày hè ngột ngạt trở nên ngọt ngào hơn.', 'Sấu không phải là loại quả có quanh năm. Mùa của sấu cũng là mùa của nắng hè. Với những trái sấu, cái oi bức nóng nực của mùa hè dường như phải nhường chỗ…  ', 5000, 'image/IMG_Douong/Douong53.jpg', 9),
(54, 'Yaourt Sữa Thanh Long ', '- 2 quả thanh long. - 50gr chuối. - 1 hủ yaourt lỏng. - 1/2 ly sữa tươi. - 1 thìa súp đường cát.', '- Cắt vát 1 mặt quả thanh long, lầy phần ruột bên trong thái miếng nhò.\r\n- Lột vỏ chuối, thái nhỏ.\r\n- Hòa tan đường và chút nước, thắng vừa vàng sóng sánh là được.\r\n- Cho thanh long, chuối và sữa chua vào máy xay sinh tố, xay vừa nát.', 'Cho hỗn hợp sinh tố vào quả thanh long đã khoét ruột, rưới nước đường lên, dùng ngay.', NULL, NULL, 9),
(55, 'Mướp Đắng Thập Cẩm ', '- 300gr mướp đắng. - 1 quả cam. - 1/2 quả chanh. - 1/2 thìa cà phê muối. - 1 bát nước đun sôi để nguội. - Một ít đá bào. - 1 thìa súp đường.', '- Rửa mướp đắng, bổ đôi, bỏ hạt, thái khoanh mỏng.\r\n- Bổ đôi cam, chanh, vắt lấy nước cốt, để riêng từng loại.\r\n- Cho mướp đắng, nước sôi để nguội vào máy xay sinh tố, xay nhuyễn.\r\n- Tiếp tục, cho nước cốt cam, chanh, đường, muối vào máy xay sinh tố, xay thêm 1 lần nữa.\r\nMách nhỏ:\r\nCó thể luộc sơ mướp đắng với chút muối để bớt mùi hăng.', 'Khi nào dùng, cho đá bào vào.', NULL, NULL, 9),
(56, '', NULL, NULL, NULL, NULL, NULL, 9),
(57, 'Yaourt Bí Đỏ ', '- 200gr bí đỏ. - 1/2 hũ yaourt lỏng. - 2 thìa súp vani. - 1/2 bát sữa tươi. - 3 thìa súp đường. - 1 nhúm muối. - 1 bát nước đun sôi để nguội.', '- Gọt vỏ bí đỏ, rửa sạch, thái miếng nhỏ, cho vào máy xay sinh tố cùng với chút nước, xay nhuyễn.\r\n- Lấy bí ra, cho vào nổi nấu trên lửa nhỏ, thêm đường. muối vào khuấy tan.\r\n- Bí đỏ chín, nhấc xuống, để nguội.\r\n- Cho bí, yaourt, sữa tươi, vani vào máy xay sinh tố, xay nhuyễn.', 'Đổ ra ly, ướp thật lạnh trước khi dùng.', NULL, NULL, 9),
(58, 'Nước Ép Măng Tây Thập Cẩm ', '- 300gr măng tây. - 1 quả cam. - 1/2 quả dứa. - 1 quả chanh. - 1 thìa cà phê muối. - 1 thìa cà phê đường vàng. - 1 bát nước đun sôi để nguội. - Một ít đá viên tinh khiết. - Vài cọng măng tây trang trí.', '- Rửa sạch măng tây, tước bỏ bớt xơ, thái nhỏ.\r\n- Bổ đôi cam, chanh, vắt lấy nước cốt.\r\n- Gọt vỏ dứa, bỏ mắt, cho vào máy ép lấy nước.\r\n- Cho măng tây vào máy ép lấy nước cốt.\r\n- Cho nước cốt măng tây, nước cốt cam, chanh, dứa, đường, muối vào nước đun sôi để nguội, khuấy đều.\r\nMách nhỏ:\r\nĐể ly nước thơm ngon, nên luộc măng tây trước khi ép. ', 'Cho hỗn hợp nước măng tây này vào ly, cho đá viên vào, trang trí thêm vài cọng măng tây', NULL, NULL, 9),
(59, 'Nước Ép Bắp Cải Pha Dứa ', '- 200gr bắp cải trắng. - 150gr dứa. - 2 thìa súp đường. - Đá viên.', '- Tách lá bắp cải, rửa sạch, thái nhuyễn.\r\n- Cho bắp cải vào máy xay sinh tố, xay thật nhuyễn, lọc qua rây để lấy nước.\r\n- Gọt bỏ mắt dứa, cho vào máy ép lấy nước cốt.\r\n- Trộn đều nước dứa, nước bắp cải, cho đường đã hòa tan với chút nước đun sôi để nguội vào.\r\n- Cho đá viên và hỗn hợp nước vào bình lắc thật đều.', 'Lấy nước sinh tố ra ly, dùng lạnh rất ngon.', NULL, NULL, 9),
(60, 'Nước Ép Củ Dền ', '- 600gr củ dền. - Một ít đá viên. - 2 thìa súp đường vàng.', '- Gọt vỏ củ dền, rửa sạch, để ráo, thái làm đôi.\r\n- Hòa tan đường với chút nước, cho vào nồi đun với lửa liu riu đến khi nước đường sóng sánh vàng, nhấc xuống để nguội.\r\n- Cho củ dền vào máy ép lấy nước cốt.\r\n- Kế tiếp, cho nước đường đã đun vào hỗn hợp nước cốt củ dền, hòa tan ', 'Cho nước củ dền ra ly, cho đá viên vào, dùng ngay.', NULL, NULL, 9),
(61, 'Trái Cây Trộn Nước Chanh Dây ', '- 100gr dâu tây. - 1 quả thanh long. - 1/2 quả xoài chín. - 1/2 quả cam. - 1 quả kiwi. - 5 quả nho. - 1/2 quả táo đỏ. - 2 quả chanh dây. - 2 thìa cà phê đường. - 1/2 thìa cà phê muối. - 1 cọng rau mùi tây.', '- Ngâm dâu tây với nước muối pha loãng, nhặt sạch cuống, rửa sạch lá, để ráo, bổ làm tư.\r\n- Bổ đôi thanh long, dùng thìa tròn múc từng viên nhỏ, giữ lại vỏ làm thố đựng trái cây.\r\n- Gọt vỏ xoài, dùng thìa tròn múc viên vừa ăn.\r\n- Gọt vỏ cam, tách múi, lột bỏ lớp vỏ lụa.\r\n- Gọt vỏ kiwi, thái múi nhỏ.\r\n- Rửa sạch nho, bổ đôi, tách bỏ hạt.\r\n- Rửa táo, bỏ lõi và hạt, dùng dao răng cưa xắn múi nhỏ.\r\n- Bổ đôi chanh dây, lấy 1 thìa súp ruột để riêng, phần còn lại cho vào máy ép lấy nước cốt.\r\n- Hòa tan nước cốt chanh dây với muối, đường.\r\n- Cho trái cây vào vỏ thanh long, rưới nước cốt chanh dây, trang trí thêm hạt chanh dây, rau mùi tây. ', 'Dùng tráng miệng rất ngon.', NULL, NULL, 9),
(62, 'thanh nhiet', 'sendsacool\r\nnước loc', 'pha đi', 'giai nhiet', 0, '/image/IMG_Douong/62.jpg', 0);

